import React, { Component, useState } from "react";
import { withAuth } from "utils/auth";

class SecurePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showPassword: false, // State to control password visibility
    };
  }

  // Function to toggle password visibility
  togglePasswordVisibility = () => {
    this.setState((prevState) => ({
      showPassword: !prevState.showPassword,
    }));
  };

  render() {
    const { user } = this.props.auth; // Access current user from auth context
    const { email } = user || {}; // Destructure email from user object
    const { showPassword } = this.state;

    return (
      <div style={{ maxWidth: "600px", margin: "auto" }}>
        <h1 style={{ textAlign: "center", marginBottom: "20px", color: "#333" }}>Secure Page</h1>
        <div style={{ border: "1px solid #ccc", borderRadius: "5px", padding: "20px", backgroundColor: "#f9f9f9", boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)" }}>
          <div style={{ textAlign: "center", marginBottom: "20px", fontSize: "1.2rem" }}>
            <p><strong>Email:</strong> {email}</p>
            {/* Conditionally render password with toggle button */}
            {showPassword ? (
              <p><strong>Password:</strong> {user.password}</p> // Assuming password is accessible from user object
            ) : (
              <p><strong>Password:</strong> *********</p> // Hide password by default
            )}
            {/* Toggle button to show/hide password */}
            <button onClick={this.togglePasswordVisibility} style={{ padding: "5px 10px", backgroundColor: "#007bff", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer", marginTop: "10px" }}>
              {showPassword ? "Hide Password" : "Show Password"}
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default withAuth(SecurePage);
