import React, { useState, useEffect } from "react";
import { useAuth, withAuth } from "utils/auth";
import { UserApi } from "services";

const bulletIcon = <span className="bullet">&#8226;</span>;
const highlightedBulletIcon = <span className="bullet highlighted">&#8226;</span>;

const Profile = () => {
  const auth = useAuth();
  const [userData, setUserData] = useState(null);
  const [comment, setComment] = useState("");
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    async function loadUserData() {
      try {
        const api = new UserApi();
        api.setup();
        const response = await api.getUserById(auth.token, auth.decodedToken.id);

        if (response.kind === "ok") {
          setUserData(response.user);
        } else {
          console.error("Error fetching user data:", response.error);
        }
      } catch (err) {
        console.error("Error fetching user data:", err);
      }
    }
    loadUserData();
  }, []);

  const { id, firstName, lastName, email, admin } = userData || {};

  const handleSaveComment = async () => {
    setIsSaving(true);
    try {
      const api = new UserApi();
      api.setup();
      const response = await api.updateUserComment(auth.token, auth.decodedToken.id, comment);
      
      if (response.kind === "ok") {
        console.log("Comment saved successfully!");
      } else {
        console.error("Error saving comment:", response.error);
      }
    } catch (err) {
      console.error("Error saving comment:", err);
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>Profile</h1>
        <p>User Details</p> {/* Changed "Information" to "User Details" */}
      </div>
      {userData ? (
        <div className="profile-info-container">
          <div className="profile-info-item">
            <strong>ID:</strong> {id}
          </div>
          <div className="profile-info-item">
            <strong>First Name:</strong> {firstName}
          </div>
          <div className="profile-info-item">
            <strong>Last Name:</strong> {lastName}
          </div>
          <div className="profile-info-item">
            <strong>Email:</strong> {email}
          </div>
          <div className="profile-info-item">
            <strong>Is admin:</strong>{" "}
            {admin ? (
              <span>
                {highlightedBulletIcon} Yes
              </span>
            ) : (
              <span>
                {bulletIcon} No
              </span>
            )}
          </div>
          <div className="additional-info">
            <h2>Additional Information</h2>
            <textarea
              className="comment-textarea"
              rows="4"
              cols="50"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Add your comment here..."
            />
            <button
              className="save-button"
              onClick={handleSaveComment}
              disabled={isSaving}
            >
              {isSaving ? "Saving..." : "Save Comment"}
            </button>
          </div>
        </div>
      ) : (
        <div className="loading-text">Loading...</div>
      )}
    </div>
  );
};

export default withAuth(Profile);
