import React from "react";
import styled from "styled-components";



const FooterContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  max-width: 1200px; /* Limit width to prevent stretching on larger screens */
  margin: 0 auto; /* Center align content */
`;

interface FooterLinkProps {
  href: string;
  target: string;
  rel: string;
}

const FooterLink = styled.a<FooterLinkProps>`
  color: #ffffff;
  text-decoration: none;
  transition: color 0.3s ease;

  &:hover {
    color: #cccccc;
  }
`;

const Footer = () => {
  return (
    <footer className="bg-dark">
      <div>
        <span>&#169; {new Date().getFullYear()} Company Inc. All Rights Reserved</span>
        <div>
          <FooterLink
            href="https://github.com/martindavid/flask-react-docker-app"
            target="_blank"
            rel="noopener noreferrer"
          >
            Github
          </FooterLink>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
