import React, { useState, useEffect } from "react";
import { useAuth, withAuth } from "utils/auth";
import { UserApi } from "services";

const Profile = () => {
  const auth = useAuth();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadUserData() {
      try {
        const api = new UserApi();
        api.setup();
        const response = await api.getUserById(
          auth.token,
          auth.decodedToken.id
        );

        if (response.kind === "ok") {
          setUserData(response.user);
        } else {
          console.error("Error fetching user data:", response.error);
        }
      } catch (err) {
        console.error("Error fetching user data:", err);
      } finally {
        setLoading(false);
      }
    }
    loadUserData();
  }, []);

  const { id, firstName, lastName, email, admin } = userData || {};

  return (
    <div style={{ maxWidth: "600px", margin: "auto", marginTop: "50px" }}>
      <h1 style={{ textAlign: "center", marginBottom: "20px", fontFamily: "Arial, sans-serif" }}>Profile</h1>
      {loading ? (
        <div style={{ textAlign: "center" }}>Loading...</div>
      ) : (
        <div style={{ border: "1px solid #ccc", borderRadius: "5px", padding: "20px" }}>
          <div style={{ marginBottom: "10px" }}>
            <strong>ID:</strong> {id}
          </div>
          <div style={{ marginBottom: "10px" }}>
            <strong>First Name:</strong> {firstName}
          </div>
          <div style={{ marginBottom: "10px" }}>
            <strong>Last Name:</strong> {lastName}
          </div>
          <div style={{ marginBottom: "10px" }}>
            <strong>Email:</strong> {email}
          </div>
          <div>
            <strong>Is admin:</strong>{" "}
            <span style={{ fontWeight: "bold" }}>
              {admin ? (
                <>
                  <span style={{ color: "green" }}>Yes</span>{" "}
                  <span style={{ color: "gray" }}>No</span>
                </>
              ) : (
                <>
                  <span style={{ color: "gray" }}>Yes</span>{" "}
                  <span style={{ color: "red" }}>No</span>
                </>
              )}
            </span>
          </div>
        </div>
      )}
    </div>
  );
};

export default withAuth(Profile);
