import React, { useState, useEffect } from "react";
import { UserApi } from "services";

const Index = () => {
  return (
    <div className="row">
      <div className="col-12">
        <h1>Home Page</h1>
        <Home />
      </div>
    </div>
  );
};

export default Index;

export const Home = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    async function fetchUsers() {
      try {
        const api = new UserApi();
        api.setup();
        const response = await api.getUsers();
        if (response.kind === "ok") {
          setUsers(response.users);
        } else {
          console.error("Error fetching users:", response.error);
        }
      } catch (err) {
        console.error("Error fetching users:", err);
      } finally {
        setLoading(false); // Set loading state to false after fetching
      }
    }
    fetchUsers();
  }, []);

  return (
    <div style={{ maxWidth: "800px", margin: "auto" }}>
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>User List</h1>
      {loading ? ( // Display loading message while fetching data
        <div style={{ textAlign: "center" }}>Loading...</div>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th style={{ border: "1px solid #ccc", padding: "8px" }}>Name</th>
              <th style={{ border: "1px solid #ccc", padding: "8px" }}>Email</th>
              <th style={{ border: "1px solid #ccc", padding: "8px" }}>Status</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={index}>
                <td style={{ border: "1px solid #ccc", padding: "8px" }}>{user.firstName} {user.lastName}</td> 
                <td style={{ border: "1px solid #ccc", padding: "8px" }}>{user.email}</td>
                <td style={{ border: "1px solid #ccc", padding: "8px" }}>{user.admin ? "Admin" : "No"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};
