import React, { useState, useEffect } from "react";
import { useAuth, withAuth } from "utils/auth";
import { UserApi } from "services";

const Profile = () => {
  const auth = useAuth();
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    async function loadUserData() {
      try {
        const api = new UserApi();
        api.setup();
        console.log("Token:", auth.token); // Log the token
        console.log("Decoded Token:", auth.decodedToken); // Log the decoded token ID

        // Check if the token has expired
        const isTokenExpired = auth.decodedToken && auth.decodedToken.exp < Date.now() / 1000;
        if (isTokenExpired) {
          console.log("Token has expired. Please log in again.");
          // Handle token expiration (e.g., redirect to login page)
          return;
        }

        const response = await api.getUserById(
          auth.token,
          auth.decodedToken.id
        );

        if (response.kind === "ok") {
          console.log("User Data:", response.user); // Log the retrieved user data
          setUserData(response.user);
        }
      } catch (err) {
        console.log(err);
      }
    }
    loadUserData();
  }, [auth.token, auth.decodedToken]); // Include auth.token and auth.decodedToken in dependency array

  return (
    <div className="row">
      <div className="col-12">
        <h1>Profile</h1>
        <div>
          {userData ? (
            <>
              <div>Id: {userData.id}</div>
              <div>First Name: {userData.firstName}</div>
              <div>Last Name: {userData.lastName}</div>
              <div>Email: {userData.email}</div>
              <div>Is admin: {userData.admin ? "Yes" : "No"}</div>
            </>
          ) : (
            <div>Loading...</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default withAuth(Profile);
