import React, { useState, useEffect } from "react";
import { useAuth, withAuth } from "utils/auth";
import { UserApi } from "services";
import { Card, Spinner } from "react-bootstrap"; // Assuming Bootstrap for styling

const Profile = () => {
  const auth = useAuth();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadUserData() {
      try {
        const api = new UserApi();
        api.setup();
        const response = await api.getUserById(
          auth.token,
          auth.decodedToken.id
        );

        if (response.kind === "ok") {
          setUserData(response.user);
        } else {
          console.error("Error fetching user data:", response.error);
        }
      } catch (err) {
        console.error("Error fetching user data:", err);
      } finally {
        setLoading(false);
      }
    }
    loadUserData();
  }, []);

  const { id, firstName, lastName, email, admin } = userData || {};

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Profile</h1>
      {loading ? (
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="sr-only">Loading...</span>
          </Spinner>
        </div>
      ) : (
        <Card>
          <Card.Body>
            <div className="mb-3">
              <strong>ID:</strong> {id}
            </div>
            <div className="mb-3">
              <strong>First Name:</strong> {firstName}
            </div>
            <div className="mb-3">
              <strong>Last Name:</strong> {lastName}
            </div>
            <div className="mb-3">
              <strong>Email:</strong> {email}
            </div>
            <div className="mb-0">
              <strong>Is admin:</strong>{" "}
              <span className={admin ? "text-success" : "text-danger"}>
                {admin ? "Yes" : "No"}
              </span>
            </div>
          </Card.Body>
        </Card>
      )}
    </div>
  );
};

export default withAuth(Profile);
